import React, { useContext } from 'react'
import noteContext from '../context/notes/NoteContext';

const Note = ({note, updatenote}) => {

    const context = useContext(noteContext);
    const { deletenote } = context;

    //const {note} = props;
    let tagcolor = "";

    if(note.tag === "G")
    {
        tagcolor="primary"
    }
    else if(note.tag === "P")
    {
        tagcolor="danger"
    }
    else
    {
        tagcolor="warning"

    }


    return (
        <div className={`col-md-4 `}>
            <div className={`card my-3 border-5 border-${tagcolor}`}>
                    <div className="card-body">
                        <h5 className="card-title">{note.title}</h5>
                        <p className="card-text">{note.description}</p>
                        <i className="far fa-edit mx-2 text-primary" onClick={(note)=>{updatenote()}}></i>
                        <i className="far fa-trash-alt mx-2 text-danger" onClick={()=>deletenote(note._id)}></i>
                    </div>
            </div>
        </div>
    )
}

export default Note
