import React, { useState } from "react";
import NoteContext from "./NoteContext";

const NoteState = (props) => {

  const host = "http://localhost:5000";

  const notestInitial = []

  const [notes, setNotes] = useState(notestInitial);


  // Get all notes
  const getnotes = async () => {

    const response = await fetch(`${host}/api/notes/fetchnotes`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'auth-tocken': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjI0NWM3NjllMzAyNTMyYTVkZThjMzJhIn0sImlhdCI6MTY0ODc0MzI3N30.lVWnYRRjlEnM4ZlK7bvjworXXKFr8lXbqWpCWp-bMX8'

      },

    });
    const json = await response.json();
    // console.log(json);
    setNotes(json);

  }

  //Add note

  const addnote = async (title, description, tag) => {

    const response = await fetch(`http://localhost:5000/api/notes/addnote`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'auth-tocken': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjI0NWM3NjllMzAyNTMyYTVkZThjMzJhIn0sImlhdCI6MTY0ODc0MzI3N30.lVWnYRRjlEnM4ZlK7bvjworXXKFr8lXbqWpCWp-bMX8'

      },
      body: JSON.stringify({title, description, tag})
    });



   // console.log("adding a new note");
    const note = {
      "_id": "624614c821ecxxaad0a4f1c733",
      "user": "6245c769e302532a5de8c32a",
      "title": title,
      "description": description,
      "tag": tag,
      "date": "2022-03-31T20:53:28.105Z",
      "__v": 1
    };

    setNotes(notes.concat(note));

  }

  // Edit note
  const editnote = async (id, title, description, tag) => {


    const response = await fetch(`${host}/api/notes/updatenote/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        'auth-tocken': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjI0NWM3NjllMzAyNTMyYTVkZThjMzJhIn0sImlhdCI6MTY0ODc0MzI3N30.lVWnYRRjlEnM4ZlK7bvjworXXKFr8lXbqWpCWp-bMX8'

      },
      body: JSON.stringify({ title, description, tag })
    });


    for (let index = 0; index < notes.length; index++) {
      const element = notes[index];
      if (element._id === id) {
        notes[index].title = title
        notes[index].description = description
        notes[index].tag = tag
        break;
      }
    }

    setNotes(notes);


  }

  // deleting note
  const deletenote = async(id) => {

    const response = await fetch(`${host}/api/notes/deletenote/${id}`, {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json',
        'auth-tocken': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjI0NWM3NjllMzAyNTMyYTVkZThjMzJhIn0sImlhdCI6MTY0ODc0MzI3N30.lVWnYRRjlEnM4ZlK7bvjworXXKFr8lXbqWpCWp-bMX8'

      },

    });



    const newnotes = notes.filter((note) => { return (note._id !== id) })
    // console.log(`"deleting ${id}"`);
    setNotes(newnotes);
  }


  return (
    <NoteContext.Provider value={{ notes, addnote, editnote, deletenote, getnotes }}>
      {props.children}
    </NoteContext.Provider>
  )
}

export default NoteState;
