import React, { useEffect } from 'react'
import { Link, useLocation, useNavigate } from "react-router-dom";
import logo from './icon.png'
const Navbar = () => {

    let location = useLocation();
    const navigate = useNavigate();

    useEffect(() => {
        //console.log(location.pathname);
    }, [location])

    const handlelogout = () => {
        localStorage.removeItem('tocken');
        navigate('/login');
    }

    return (
        <div>
            <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
                <div className="container-fluid">
                    <Link class="navbar-brand d-flex" to="/">
                        <img src={logo} width="40" height="30" class="d-inline-block align-top" alt=""/>
                        <h4 className='text-weight-bolder'>Cloud-Notes</h4>
                    </Link>
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                            <li className="nav-item">
                                <Link className={`nav-link ${location.pathname === "/" ? "active" : ""}`} aria-current="page" to="/">Home</Link>
                            </li>
                            <li className="nav-item">
                                <Link className={`nav-link ${location.pathname === "/about" ? "active" : ""}`} to="/about">About</Link>
                            </li>


                        </ul>
                        {!localStorage.getItem('tocken') ?

                            <div className="form-check form-switch mx-1 text-light">
                                <Link className='btn btn-primary mx-1' role='button' to="/login">Login</Link>
                                <Link className='btn btn-primary mx-1' role='button' to="/signup">SignUp</Link>
                            </div> :

                            <button className='btn btn-primary mx-1' onClick={handlelogout}>Logout</button>

                        }
                    </div>
                </div>
            </nav>
        </div>
    )
}

export default Navbar;
