import React, { useContext } from 'react'
import { Link } from 'react-router-dom';
import noteContext from '../context/notes/NoteContext';
import './css/note.css'


const Note = ({note, updatenote, readmore, setReadmorecontent}) => {

    const context = useContext(noteContext);
    const { deletenote } = context;

    //const {note} = props;
    let tagcolor = "";

    if(note.tag === "G")
    {
        tagcolor="primary"
    }
    else if(note.tag === "P")
    {
        tagcolor="danger"
    }
    else
    {
        tagcolor="warning"

    }



    // const truncate = (, n) => {
	// 	return str?.length > n ? str.substr(0, n - 1) + "..." : str;
	// };


    return (
        <div className={`col-md-4 `}>
            <div className={`card my-3 border-5 border-${tagcolor} note-card`}>
                    <div className="card-body">
                        <h5 className="card-title">{note.title}</h5>
                        <p className="card-subtitle text-muted">{note.description.length > 50 ? note.description.substr(0, 80)+"...": note.description}{note.description.length > 50 && <Link to="/read" onClick={()=>{readmore(); setReadmorecontent(note.description)}}>Read More</Link>}</p>
                        <i className="far fa-edit mx-2 text-primary" onClick={()=>{updatenote(note)}}></i>
                        <i className="far fa-trash-alt mx-2 text-danger" onClick={()=>deletenote(note._id)}></i>
                    </div>

            </div>
        </div>
    )
}

export default Note
