import noteConext from "../context/notes/NoteContext"
import React, { useContext, useEffect, useRef } from 'react'
import Note from "./Note";
import AddNotes from "./AddNotes";
import Editnote from "./Editnote";

const Main = () => {

  const [note, setNote] = useState({title:"",description:"", tag:""});

  const context = useContext(noteConext);
  const { notes, getnotes } = context;

  useEffect(() => {
    getnotes();
    // eslint-disable-next-line 
  }, [])


  const ref = useRef(null);

  const updatenote = (currentNote) => {
    ref.current.click();
    setNote(currentNote);
  };


  return (

    <>


      <button ref={ref} type="button" className="btn btn-primary d-none" data-bs-toggle="modal" data-bs-target="#exampleModal">
        Launch modal
      </button>


      <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="exampleModalLabel">Update Your Note</h5>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div className="modal-body">
              <Editnote setNote={setNote}/>
            </div>
          </div>
        </div>
      </div>

      <AddNotes />
      <h2>Your Notes</h2>
      <div className="row my-3">

        {notes.map((note) => {

          return <Note key={note._id} note={note} updatenote={updatenote}/>

        })}
      </div>
    </>
  )
}

export default Main;
