import {React ,useContext, useRef, useState} from 'react'
import noteConext from "../context/notes/NoteContext"

const AddNotes = () => {

    const context = useContext(noteConext);
    const { addnote } = context;

    const [note, setNote] = useState({title:"",description:"", tag:"", image:""});
    const [fileName, setFileName] = useState("");

    const ref = useRef();


    const onChangeFile = (e) =>{

        const nm = (e.target.files[0].name);
        setFileName(nm);
        console.log(fileName);

    }


    const onChange = (e)=>{

        setNote({...note,[e.target.name]:[e.target.value]});
       // console.log(notes);

    }

    const handleAddClick = (e)=> {

        e.preventDefault();

        setNote({image : fileName});



        addnote(note.title.toString(), note.description.toString(), note.tag.toString(), note.image.toString());
        //console.log(note.title.toString(), note.description.toString(), note.tag.toString());
        setNote({title:"",description:"", tag:"", image:""});
    }

    return (

        <div>
            <div className='container my-3'>
                <h2>Add Notes</h2>
                <form onSubmit={handleAddClick} encType='multipart/form-data'>
                    <div className="mb-3">
                        <label htmlFor="exampleInputEmail1" className="form-label h4">Title</label>
                        <input type="text" className="form-control" id="title" name='title' aria-describedby="emailHelp" value={note.title} onChange={onChange} />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="description" className="form-label h4">Description</label>
                        <textarea type="text" className="form-control" id="description" name="description" value={note.description} onChange={onChange} />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="tag" className="form-label h4">Tag</label>
                        <input type="text" className="form-control" id="tag" name="tag" placeholder="G-General | P-High Priority | O-Other" value={note.tag} onChange={onChange} />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="image" className="form-label h4">Upload Image</label>
                        <input type="file" className="form-control" id="image" name="image" onChange={onChangeFile}/>
                    </div>
                    <button type="submit" className="btn btn-primary">Add</button>
                </form>

            </div>
        </div>
    )
}

export default AddNotes
