import {React ,useContext, useState} from 'react'
import noteConext from "../context/notes/NoteContext"

const AddNotes = () => {

    const context = useContext(noteConext);
    const { addnote } = context;

    const [notes, setNotes] = useState({title:"",description:"", tag:""});

    const onChange = (e)=>{

        setNotes({...notes,[e.target.name]:[e.target.value]});
       // console.log(notes);
    }

    const handleAddClick = ()=> {


    }

    return (

        <div>
            <div className='container my-3'>
                <h2>Add Notes</h2>
                <form>
                    <div class="mb-3">
                        <label htmlFor="exampleInputEmail1" class="form-label h2">Title</label>
                        <input type="text" class="form-control" id="title" name='title' aria-describedby="emailHelp" onChange={onChange} />
                    </div>
                    <div class="mb-3">
                        <label htmlFor="description" class="form-label">Description</label>
                        <textarea type="text" class="form-control" id="description" name="description" onChange={onChange} />
                    </div>
                    <div class="mb-3 form-check">
                        <input type="checkbox" class="form-check-input" id="exampleCheck1"/>
                        <label class="form-check-label" htmlFor="exampleCheck1">Check me out</label>
                    </div>
                    <button type="submit" class="btn btn-primary" onClick={handleAddClick}>Submit</button>
                </form>

            </div>
        </div>
    )
}

export default AddNotes
