import React, { useState } from 'react'
import image from './loginpage.svg'

const Login = () => {

    const [credential, setCredential] = useState({email:"", password:""})

    const handlesubmit = async (e) => {
        e.preventdefult();
        const response = await fetch(`http://localhost:5000/api/auth/login`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ email : credential.email, password : credential.password })
        });

        const json = await response.json();
        console.log(json);
    }

    const onChange = (e)=>{

        setCredential({...credential,[e.target.name]:[e.target.value]});
       // console.log(notes);
    }

    return (
        <div className='p-4 bg-success'>
            <div className="content">
                <div className="container">
                    <div className="row">
                        <div className="col-md-6">
                            <img src={image} alt="Image" className="img-fluid" />
                        </div>
                        <div className="col-md-6 contents">
                            <div className="row justify-content-center">
                                <div className="col-md-8 mt-4">
                                    <div className="mb-4">
                                        <h3>Sign In</h3>
                                        <p className="mb-4">WELCOME TO THE CLOUD-NOTES</p>
                                    </div>
                                    <form>
                                        <div className="form-group first">
                                            <label htmlFor="email">Username</label>
                                            <input type="text" className="form-control" id="email" name='email' value={credential.email} onChange={onChange}/>

                                        </div>
                                        <div className="form-group last mb-4">
                                            <label htmlFor="password">Password</label>
                                            <input type="password" className="form-control" id="password" name='password' value={credential.password} onChange={onChange}/>

                                        </div>


                                        <button onClick={handlesubmit} className="btn btn-primary">Login</button>

                                    </form>
                                </div>
                            </div>

                        </div>

                    </div>
                </div>
            </div>
        </div>
    )
}

export default Login
